this.LineColor = Utilities.HexToColor("#022672");
           FillColor = Utilities.HexToColor("#a3b4ff");
          _overlayType = MOverlayType.MPolygon;
           this.FillOpacity = 0.8;
          this.LineThickness = 3;
           this.LineType = MLineType.LineSolid;
